pragma solidity 0.4.24;






contract OracleVerifier is ChainlinkClient, Ownable {

    // solium-disable-next-line zeppelin/no-arithmetic-operations
    uint256 public oraclePayment = 1 * LINK;
    // solium-disable-next-line zeppelin/no-arithmetic-operations
    uint256 public marketPayment = 15 * LINK;

    string public baseURL;
    mapping(bytes32 => address) public requesters;

    MarketDepositsInterface public marketDeposits;

    event VerificationRequestFulfilled(address indexed oracle, bytes32 responseToken);

    constructor(address _link, address _marketDeposits, string _baseURL) public {
        setChainlinkToken(_link);
        marketDeposits = MarketDepositsInterface(_marketDeposits);
        baseURL = _baseURL;
    }

    function verificationRequest(
        address _oracle,
        bytes32 _jobId,
        string _nodeId,
        string _requestToken
    )
    external
    returns (bytes32)
    {
        Chainlink.Request memory req = buildChainlinkRequest(_jobId, this, this.fulfill.selector);
        req.add("url", string(abi.encodePacked(baseURL, "/v1/nodes/", _nodeId, "/verification/response?token=", _requestToken)));
        req.add("path", "responseToken");

        marketDeposits.spend(msg.sender, this, oraclePayment);
        bytes32 reqId = sendChainlinkRequestTo(_oracle, req, oraclePayment);
        requesters[reqId] = msg.sender;

        return reqId;
    }

    function fulfill(bytes32 _requestId, bytes32 _responseToken)
    external
    recordChainlinkFulfillment(_requestId)
    {
        marketDeposits.spend(requesters[_requestId], address(0), marketPayment);
        delete requesters[_requestId];
        emit VerificationRequestFulfilled(msg.sender, _responseToken);
    }

    function cancelRequest(
        bytes32 _requestId,
        uint256 _payment,
        bytes4 _callbackFunctionId,
        uint256 _expiration
    )
    external
    onlyOwner
    {
        cancelChainlinkRequest(_requestId, _payment, _callbackFunctionId, _expiration);
    }

    function setOraclePayment(uint256 _oraclePayment) external onlyOwner {
        oraclePayment = _oraclePayment;
    }

    function setMarketPayment(uint256 _marketPayment) external onlyOwner {
        marketPayment = _marketPayment;
    }
}
